import { useEffect, useState } from 'react'
import React, { Component } from 'react';
import { Link, Route, Switch } from 'react-router-dom'
import { useNavigate } from "react-router-dom";
import axios from 'axios'

import EthSwap from "../ABIS_CutFeeGiveOrdrId/EthSwap.json";

import Web3 from "web3";

import lockedIcon from '../Images/exchangeIcons/lockedIconLight.png';
import ftpToken from '../Images/exchangeIcons/ftpToken.png';
import etherToken from '../Images/exchangeIcons/etherToken.png';
import bnbToken from '../Images/exchangeIcons/bnbToken.png';
import toggleBtn from '../Images/exchangeIcons/toggleBtn.png';
import buyImg from '../Images/exchangeIcons/buyImg.png';
import swapImg from '../Images/exchangeIcons/swapImg.png';
import stakeImg from '../Images/exchangeIcons/stakeImg.png';
import tradeImg from '../Images/exchangeIcons/tradeImg.png';
import connectImg from '../Images/exchangeIcons/connectImg.png';
import networkDropdown from '../Images/exchangeIcons/networkDropdown.png';
import lightSetting1 from '../Images/exchangeIcons/lightSetting1.png';
import lightInfo from '../Images/exchangeIcons/lightInfo.png';
import lightRefresh from '../Images/exchangeIcons/lightRefresh.png';

// css
import './css/exchangeCss/exchange.css'
// components
import Graph from '../components/Graph.jsx'
import HeaderNav from '../components/HeaderNav.jsx';
import FooterBottom from '../components/footerBottom.jsx';
function App() {

  const navigate = useNavigate();

  const [CurrentUserNetwork, setCurrentUserNetwork] = useState('Binance');
  const [userAccount, setuserAccount] = useState('');
  const [assetsTokens, setassetsTokens] = useState([]);
  const [UsdtTokens, setUsdtTokens] = useState([]);
  const [nativeTokenBalance, setnativeTokenBalance] = useState('0');
  const [stakedBalance, setstakedBalance] = useState('0');
  const [FairtraderBlnc, setFairtraderBlnc] = useState('0');
  const [UsdtBlnc, setUsdtBlnc] = useState('0');
  const [USDTPriceOfBNB, setUSDTPriceOfBNB] = useState('0');
  const [USDTPriceOfBFTP, setUSDTPriceOfBFTP] = useState('');

  useEffect(() => {
    useEffectFunc()
    window.ethereum.on("chainChanged", networkChanged)
