                        </div>
                        : ""}
                      {/* {this.state.wantToOpenMsg.message} */}
                    </div>
                  </div>}
            {messageBtnUI}
          </div>
        </div>
        <ToastContainer />
      </div>
    );
  }
}

export default App;
