                    this.setState({ ViewAddNotePaid: false });
                    this.setState({ contractSelectedPaid: false });




                    this.setState({
                      invoicePaidBtn: false,
                      invoiceUnpaidOrder: false,
                      searchUserMagnifierViewUnpaid: false,
                      searchUserMagnifierViewPaid: false,
